$.fn.colorpicker=function(){var c=$('<ul id="colorpicker" />');var a={"light-green":"#d8f3b1",green:"#94deaf","dark-green":"#67d08d","light-blue":"#a6f8ff",blue:"#87c3eb",yellow:"#ffffb1",orange:"#ffd78b",red:"#f28f8b","light-brown":"#f1ebbb",brown:"#d0c9a4"};for(var b in a){c.append('<li style="background:'+a[b]+'" title="'+b+'">'+a[b]+"</li>")}$("body").append(c);return this.each(function(){var d=$(this);d.on("focus, click",function(){var e=$(this).offset();c.css({display:"block",top:e.top+34,left:e.left-27}).children().click(function(){d.val($(this).text()).css("background",$(this).text());d.focus()})});d.blur(function(){if(!c.is(":hover")){c.fadeOut(200)}})})};